static void* Sbk_FooClass_typeDiscovery(void* cptr, SbkObjectType* instanceType)
{
    if (instanceType == reinterpret_cast<SbkObjectType*>(Shiboken::SbkType< ::QObject >()))
        return dynamic_cast< ::FooClass*>(reinterpret_cast< ::QObject*>(cptr));
    return 0;
}


// Type conversion functions.

// Python to C++ pointer conversion - returns the C++ object of the Python wrapper (keeps object identity).
static void FooClass_PythonToCpp_FooClass_PTR(PyObject* pyIn, void* cppOut) {
    Shiboken::Conversions::pythonToCppPointer(&Sbk_FooClass_Type, pyIn, cppOut);
}
static PythonToCppFunc is_FooClass_PythonToCpp_FooClass_PTR_Convertible(PyObject* pyIn) {
    if (pyIn == Py_None)
        return Shiboken::Conversions::nonePythonToCppNullPtr;
    if (PyObject_TypeCheck(pyIn, (PyTypeObject*)&Sbk_FooClass_Type))
        return FooClass_PythonToCpp_FooClass_PTR;
    return 0;
}

// C++ to Python pointer conversion - tries to find the Python wrapper for the C++ object (keeps object identity).
static PyObject* FooClass_PTR_CppToPython_FooClass(const void* cppIn) {
    return PySide::getWrapperForQObject((::FooClass*)cppIn, &Sbk_FooClass_Type);

}

void init_FooClass(PyObject* module)
{
    SbkfoolibTypes[SBK_FOOCLASS_IDX] = reinterpret_cast<PyTypeObject*>(&Sbk_FooClass_Type);

    if (!Shiboken::ObjectType::introduceWrapperType(module, "FooClass", "FooClass*",
        &Sbk_FooClass_Type, &Shiboken::callCppDestructor< ::FooClass >, (SbkObjectType*)SbkPySide_QtCoreTypes[SBK_QOBJECT_IDX])) {
        return;
    }

    // Register Converter
    SbkConverter* converter = Shiboken::Conversions::createConverter(&Sbk_FooClass_Type,
        FooClass_PythonToCpp_FooClass_PTR,
        is_FooClass_PythonToCpp_FooClass_PTR_Convertible,
        FooClass_PTR_CppToPython_FooClass);

    Shiboken::Conversions::registerConverterName(converter, "FooClass");
    Shiboken::Conversions::registerConverterName(converter, "FooClass*");
    Shiboken::Conversions::registerConverterName(converter, "FooClass&");
    Shiboken::Conversions::registerConverterName(converter, typeid(::FooClass).name());
    Shiboken::Conversions::registerConverterName(converter, typeid(::FooClassWrapper).name());


    Shiboken::ObjectType::setTypeDiscoveryFunctionV2(&Sbk_FooClass_Type, &Sbk_FooClass_typeDiscovery);

    PySide::Signal::registerSignals(&Sbk_FooClass_Type, &::FooClass::staticMetaObject);

    FooClassWrapper::pysideInitQtMetaTypes();
    Shiboken::ObjectType::setSubTypeInitHook(&Sbk_FooClass_Type, &PySide::initQObjectSubType);
    PySide::initDynamicMetaObject(&Sbk_FooClass_Type, &::FooClass::staticMetaObject, sizeof(::FooClass));
}
